// Sub-components
interface ProductBlockProps {
  item: NavMenuItem;
  onClick?: () => void;
  className?: string;
}

function ProductBlock({ item, onClick, className }: ProductBlockProps) {
  const IconComponent = productIcons[item.id] || BridgeIcon;
  const gradient = iconGradients[item.id] || iconGradients.bridge;
  const isDisabled = !item.href;

  const handleClick = () => {
    if (isDisabled) return;
    if (item.href) {
      window.open(item.href, "_blank", "noopener,noreferrer");
    }
    item.onClick?.();
    onClick?.();
  };

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === "Enter" || e.key === " ") {
      e.preventDefault();
      handleClick();
    }
  };

  return (
    <div
      className={cn(
        productBlockVariants(),
        isDisabled && "opacity-50 cursor-not-allowed",
        className
      )}
      onClick={handleClick}
      role="button"
      tabIndex={isDisabled ? -1 : 0}
      onKeyDown={handleKeyDown}
      aria-disabled={isDisabled}
    >
      {/* Icon container with gradient background */}
      <div
        className={cn(
          "relative shrink-0 size-16 rounded-lg flex items-center justify-center",
          isDisabled && "grayscale"
        )}
        style={{ backgroundImage: gradient }}
      >
        <IconComponent />
      </div>

      {/* Text content */}
      <div className="flex flex-col gap-1 flex-1 min-w-0">
        <p className="font-heading font-bold text-base text-white tracking-wider leading-tight">
          {item.title}
        </p>
        <p className="font-body text-sm text-white/60 leading-relaxed">
          {item.description}
        </p>
      </div>
    </div>
  );
}

// Desktop content component
