/**
 * GlobalNavMenu component - A full-screen navigation menu triggered by an icon button.
 *
 * Features:
 * - Responsive design with separate desktop and mobile layouts
 * - Uses Sheet component for slide-down animation
 * - Displays product/app navigation items with icons and descriptions
 * - Desktop includes video placeholder and Move Alliance section
 *
 * @example
 * ```tsx
 * <GlobalNavMenu
 *   items={[
 *     { id: 'bridge', title: 'BRIDGE', description: '...' },
 *     { id: 'staking', title: 'STAKING', description: '...' },
 *   ]}
 *   onItemClick={(item) => console.log('Clicked:', item.id)}
 * />
 * ```
 */
function GlobalNavMenu({
  items = defaultNavItems,
  trigger,
  showVideoSection = true,
  showAllianceSection = true,
  movePrice = "$0.03",
  onItemClick,
  triggerClassName,
  className,
}: GlobalNavMenuProps) {
  const isMobile = useIsMobile();

  const defaultTrigger = (
    <button
      type="button"
      className={cn(
        "size-10 inline-flex items-center justify-center cursor-pointer transition-colors group",
        triggerClassName
      )}
      aria-label="Open navigation menu"
    >
      <GridIcon className="fill-white group-hover:fill-guild-green-400" />
    </button>
  );

  return (
    <Sheet>
      <SheetTrigger asChild>{trigger || defaultTrigger}</SheetTrigger>
      <SheetContent
        side="top"
        hideCloseButton
        className={cn(
          "p-0 border-0 bg-black w-full max-w-none data-[state=open]:duration-300 data-[state=closed]:duration-200",
          isMobile ? "h-screen" : "h-auto max-h-[700px]",
          className
        )}
      >
        {/* Custom close button */}
        <SheetClose className="absolute top-8 right-8 z-10 border-0 rounded-sm opacity-70 transition-opacity hover:opacity-100 focus:outline-none focus:ring-2 focus:ring-white/20 focus:ring-offset-2 focus:ring-offset-black disabled:pointer-events-none">
          <X size={24} weight="bold" className="text-white" />
          <span className="sr-only">Close</span>
        </SheetClose>

        {isMobile ? (
          <MobileMenuContent items={items} onItemClick={onItemClick} />
        ) : (
          <DesktopMenuContent
            items={items}
            showVideoSection={showVideoSection}
            showAllianceSection={showAllianceSection}
            movePrice={movePrice}
            onItemClick={onItemClick}
          />
        )}
      </SheetContent>
    </Sheet>
  );
}

export { GlobalNavMenu, productBlockVariants };
export type { ProductBlockProps, DesktopMenuContentProps, MobileMenuContentProps };
